def get_items_by_category(pocket_id, language_id = 9)
	DB.execute("select n.item_id, n.name, e.effect, e.short_effect, min(f.flavor_text) as flavor_text, group_concat(vn.name, '~') as versions, s.sprites, i.cost from pokemon_v2_item i join pokemon_v2_itemcategory c on i.item_category_id = c.id join pokemon_v2_itempocket p on c.item_pocket_id = p.id join pokemon_v2_itemname n on i.id = n.item_id join pokemon_v2_itemflavortext f on i.id = f.item_id join pokemon_v2_version v on f.version_group_id = v.version_group_id join pokemon_v2_versionname vn on v.id = vn.version_id and vn.language_id = ? join pokemon_v2_itemeffecttext e on i.id = e.item_id join pokemon_v2_itemsprites s on i.id = s.item_id where p.id = ? and n.language_id = ? and f.language_id = ? and e.language_id = ? group by n.item_id;", [ language_id, pocket_id, language_id, language_id, 9 ])
end
