def get_pokemon_moves_hmtm(pokemon_id, generation_id, language_id = 9)
	DB.execute("select pokemonmove.version_group_id, vn.name, machine.machine_number, name.name, move.type_id, typename.name, movedmgname.name, move.move_damage_class_id, move.power, move.accuracy, pokemonmove.move_id, (movedmgname.move_damage_class_id <> 1 and move.type_id in (select type_id from pokemon_v2_pokemontype where pokemon_id = ?)) as is_stab, vg.generation_id, genname.name from pokemon_v2_pokemonmove pokemonmove join pokemon_v2_move move on move.id = pokemonmove.move_id join pokemon_v2_movename name on name.move_id = move.id and name.language_id = ? join pokemon_v2_movedamageclassname movedmgname on movedmgname.move_damage_class_id = move.move_damage_class_id and movedmgname.language_id = ? join pokemon_v2_versiongroup vg on vg.id = pokemonmove.version_group_id join pokemon_v2_generationname genname on genname.generation_id = vg.generation_id join pokemon_v2_typename typename on typename.type_id = move.type_id join pokemon_v2_machine machine on machine.move_id = pokemonmove.move_id and machine.version_group_id = vg.id join pokemon_v2_versionname vn on vn.version_id = machine.version_group_id where pokemonmove.pokemon_id = ? and pokemonmove.move_learn_method_id = 4 and genname.language_id = ? and typename.language_id = ? and vn.language_id = ? and vg.generation_id = ? order by pokemonmove.version_group_id, machine.machine_number asc, name.name asc;", [ pokemon_id, language_id, language_id, pokemon_id, language_id, language_id, language_id, generation_id ])
end
